package domaine;

import java.util.Collection;

public class Club {
	
	private int clubId;
	private String nomDuClub;
	private Collection<Personne> membres;
	
	public int getClubId() {
		return clubId;
	}
	
	public Club() {
		super();
		// TODO Auto-generated constructor stub
	}

	public Club(int clubId, String nomDuClub, Collection<Personne> membres) {
		super();
		this.clubId = clubId;
		this.nomDuClub = nomDuClub;
		this.membres = membres;
	}

	public void setClubId(int clubId) {
		this.clubId = clubId;
	}
	public String getNomDuClub() {
		return nomDuClub;
	}
	public void setNomDuClub(String nomDuClub) {
		this.nomDuClub = nomDuClub;
	}
	public Collection<Personne> getMembres() {
		return membres;
	}
	public void setMembres(Collection<Personne> membres) {
		this.membres = membres;
	}
	
	@Override
	public String toString() {
		return "Club [clubId=" + clubId + ", nomDuClub=" + nomDuClub + ", membres=" + membres + "]";
	}
}
